import allStates from "./allstates.json";
import './Map.css';
import React, { useState ,useEffect} from 'react';
import axios from 'axios';
import WillitendService from '../services/Willitend.service.js';
import { geoCentroid } from "d3-geo";
import {
  ComposableMap,
  Geographies,
  Geography,
  Marker,
  Annotation
} from "react-simple-maps";

const geoUrl = "https://cdn.jsdelivr.net/npm/us-atlas@3/states-10m.json";

const offsets = {
  VT: [50, -8],
  NH: [34, 2],
  MA: [30, -1],
  RI: [28, 2],
  CT: [35, 10],
  NJ: [34, 1],
  DE: [33, 0],
  MD: [47, 10],
  DC: [49, 21]
};

const Map = ({ setToolTipContent, statesData }) => { 
  const [stateInfo, setStatInfo] = useState([]);

  useEffect(() => {
    getStateInfo();
  }, []);

  const getStateInfo = () => {
    WillitendService.getAllStateInfo()
    .then(return_data =>{
      setStatInfo(return_data.data)
    })
    .catch(err=> alert(err));
  };

function getStateColor (id, name) {

  /* decide the state color using id and name and the avail datas */ 
  if (name === 'California') {
    return "#CBC3E3";
  } else if (name === "Texas") {
    return "#CBC3E3"
  }
  else {
    return "#ECDFDC";
  }

}

  return (
    <div>
    <ComposableMap data-tip ="" projection="geoAlbersUsa">
      <Geographies geography={geoUrl}>
        {({ geographies }) => (
          <>
            {geographies.map(geo => (

             <Geography
                key={geo.rsmKey}
                stroke="white"
                geography={geo}

                fill = {getStateColor(geo.id, geo.properties.name)}
                data-tip data-for={geo.id}
                onMouseEnter = { () => {

                  const name = geo.properties.name; 
                  const id = geo.id;
                  {console.log(name)} 

                  const data = "Eric";              

                  return (
                  setToolTipContent(`<h3>${data}</h3>
                   <p style ="display: inline;"><h2 style = "display: inline;">${id} DAYS</h2>    to Herd Immunity</p>`));
                }}
                onMouseLeave = { () =>{
                  setToolTipContent("");
                }}
                style={{
                    hover: {
                      stroke: "black"
                    }
                  }}
              />
            ))}
            {geographies.map(geo => {
              const centroid = geoCentroid(geo);
              const cur = allStates.find(s => s.val === geo.id);
              return (
                <g key={geo.rsmKey + "-name"}>
                  {cur &&
                    centroid[0] > -160 &&
                    centroid[0] < -67 &&
                    (Object.keys(offsets).indexOf(cur.id) === -1 ? (
                      <Marker coordinates={centroid}>
                        <text y="2" fontSize={14} textAnchor="middle">
                          {cur.id}
                        </text>
                      </Marker>
                    ) : (
                      <Annotation
                        subject={centroid}
                        dx={offsets[cur.id][0]}
                        dy={offsets[cur.id][1]}
                      >
                        <text x={4} fontSize={14} alignmentBaseline="middle">
                          {cur.id}
                        </text>
                      </Annotation>
                    ))}
                </g>
              );
            })}
          </>
        )}
      </Geographies>
    </ComposableMap>
    </div>
  );
};

export default Map;
