import React from 'react';
import ReactECharts from 'echarts-for-react';
const MapEcharts = (stateInfo) => {
  let stateName =[];
  let remainingDays =[];
  for(let i = 0;i<stateInfo.length;i++){
      console.log(i);
      stateName.push(stateInfo[i].name);
      remainingDays.push(stateInfo[i].hdays);
  }
  let option = {
    title: {
      text: 'Remaining Days For Each State Reach Herd Immunity'
    },
    tooltip: {},
    legend: {
      data:['Days']
    },
    xAxis: {
      data: stateName
    },
    yAxis: {},
    series: [{
      name: 'Days',
      type: 'bar',
      data: remainingDays
    }]
  };
  return <ReactECharts
    option={option}
    style={{ height: 400 }}
    opts={{ renderer: 'svg' }}
  />;
};
export default MapEcharts;
