import allStates from "./allstates.json";
import './Map.css';
import React, { useState ,useEffect} from 'react';
import axios from 'axios';
import WillitendService from '../services/Willitend.service.js';
import { geoCentroid } from "d3-geo";
import {
  ComposableMap,
  Geographies,
  Geography,
  Marker,
  Annotation
} from "react-simple-maps";

const geoUrl = "https://cdn.jsdelivr.net/npm/us-atlas@3/states-10m.json";

const offsets = {
  VT: [50, -8],
  NH: [34, 2],
  MA: [30, -1],
  RI: [28, 2],
  CT: [35, 10],
  NJ: [34, 1],
  DE: [33, 0],
  MD: [47, 10],
  DC: [49, 21]
};

const map = {
  "Alabama": "AL",
  "Alaska" : "AK",
  "American Samoa" : "AS",
  "Arizona" : "AZ",
  "Arkansas" : "AR",
  "Armed Forces Americas" : "AA",
  "Armed Forces Europe" : "AE",
  "Armed Forces Pacific" : "AP",
  "California" : "CA",
  "Colorado" : "CO",
  "Connecticut" : "CT",
  "Delaware" : "DE",
  "District Of Columbia" : "DC",
  "Florida" : "FL",
  "Georgia" : "GA",
  "Guam" : "GU",
  "Hawaii" : "HI",
  "Idaho" : "ID",
  "Illinois" : "IL",
  "Indiana" : "IN",
  "Iowa" : "IA",
  "Kansas" : "KS",
  "Kentucky" : "KY",
  "Louisiana" : "LA",
  "Maine" : "ME",
  "Marshall Islands" : "MH",
  "Maryland" : "MD",
  "Massachusetts" : "MA",
  "Michigan" : "MI",
  "Minnesota" : "MN",
  "Mississippi" : "MS",
  "Missouri" : "MO",
  "Montana" : "MT",
  "Nebraska" : "NE",
  "Nevada" : "NV",
  "New Hampshire" : "NH",
  "New Jersey" : "NJ",
  "New Mexico" : "NM",
  "New York" : "NY",
  "North Carolina" : "NC",
  "North Dakota" : "ND",
  "Northern Mariana Islands" : "NP",
  "Ohio" : "OH",
  "Oklahoma" : "OK",
  "Oregon" : "OR",
  "Pennsylvania" : "PA",
  "Puerto Rico" : "PR",
  "Rhode Island" : "RI",
  "South Carolina" : "SC",
  "South Dakota" : "SD",
  "Tennessee" : "TN",
  "Texas" : "TX",
  "US Virgin Islands" : "VI",
  "Utah" : "UT",
  "Vermont" : "VT",
  "Virginia" : "VA",
  "Washington" : "WA",
  "West Virginia" : "WV",
  "Wisconsin" : "WI",
  "Wyoming" : "WY"
}

var is_load_failed = false;
var is_load = false;

const Map = ({ setToolTipContent, statesData }) => { 
  const [stateInfo, setStatInfo] = useState([]);
  // Only get information from database at the first time.
  useEffect(() => {
    WillitendService.getAllStateInfo()
    .then(return_data =>{
    setStatInfo(return_data.data)
    })
    .catch(err=>{
      alert(err);
      is_load_failed = true;
      console.log("Unable to Loaded");
      }
    );
    is_load = true;
  }, []);

  const getStateInfo = (temp_name) => {

    if(is_load &&  stateInfo.length === 59 ){
      for(var i =0;i<stateInfo.length;i++){
        if(stateInfo[i].name === temp_name) return stateInfo[i];
      }
    }
    else{
      var err_message = {
        "hdays" : -99,
        "firstShot" : -99,
        "secShot" : -99,
        "vacPerDay" : -99
      };
      return err_message;
    }
  };

// Assign Color to difference states 
// based on how many day to herd immunity
function getStateColor (id, temp_name) {
  if(is_load_failed) return "#4db6ac";
  else{
    for(var i =0;i<stateInfo.length;i++){
      if(temp_name === stateInfo[i].name){
        if(stateInfo[i].hdays >=100 ) return "#fff8e1";
        else if(stateInfo[i].hdays >=80 ) return "#b2dfdb";
        else if(stateInfo[i].hdays >=60 ) return "#4db6ac"
        else if(stateInfo[i].hdays >=40 ) return "#ef9a9a";
        else return "#b2dfdb";
      }
    }
  }
}
  return (
    <div>
    <ComposableMap data-tip ="" projection="geoAlbersUsa">
      <Geographies geography={geoUrl}>
        {({ geographies }) => (
          <>
            {geographies.map(geo => (
             <Geography
                key={geo.rsmKey}
                stroke="white"
                geography={geo}
                fill = {getStateColor(geo.id, map[geo.properties.name])}
                data-tip data-for={geo.id}
                onMouseEnter = { () => {
                  const name = geo.properties.name; 
                  const id = geo.id;
                  const state_vacc_data = getStateInfo(map[name]);
                  return (
                  setToolTipContent(`<h3>State of ${name}</h3>
                   <h1 >${state_vacc_data.hdays} DAYS to Herd Immunity.</h1>
                   <h3 >${state_vacc_data.firstShot} residents are partially vaccinated.</h3>
                   <h3 >${state_vacc_data.secShot} residents are fully vaccinated.</h3>
                   <h3 >${state_vacc_data.vacPerDay} residents will be vaccinated per day.</h3> 
                   `));
                }}
                onMouseLeave = { () =>{
                  setToolTipContent("");
                }}
                style={{
                    hover: {
                      stroke: "black"
                    }
                  }}
              />
            ))}
            {geographies.map(geo => {
              const centroid = geoCentroid(geo);
              const cur = allStates.find(s => s.val === geo.id);
              return (
                <g key={geo.rsmKey + "-name"}>
                  {cur &&
                    centroid[0] > -160 &&
                    centroid[0] < -67 &&
                    (Object.keys(offsets).indexOf(cur.id) === -1 ? (
                      <Marker coordinates={centroid}>
                        <text y="2" fontSize={14} textAnchor="middle">
                          {cur.id}
                        </text>
                      </Marker>
                    ) : (
                      <Annotation
                        subject={centroid}
                        dx={offsets[cur.id][0]}
                        dy={offsets[cur.id][1]}
                      >
                        <text x={4} fontSize={14} alignmentBaseline="middle">
                          {cur.id}
                        </text>
                      </Annotation>
                    ))}
                </g>
              );
            })}
          </>
        )}
      </Geographies>
    </ComposableMap>
    </div>
  );
};

export default Map;
